export declare const listsStyles = "\n  ol.enumerate, ul.itemize {\n    padding-inline-start: 40px;\n  }\n/* It's commented because counter not supporting to change value \n  ol.enumerate.lower-alpha {\n    counter-reset: item ;\n    list-style-type: none !important;\n  }\n  .enumerate.lower-alpha > li {\n    position: relative;\n  }\n  .enumerate.lower-alpha > li:before { \n    content: \"(\"counter(item, lower-alpha)\")\"; \n    counter-increment: item; \n    position: absolute;\n    left: -47px;\n    width: 47px;\n    display: flex;\n    justify-content: flex-end;\n    padding-right: 7px;\n    flex-wrap: nowrap;\n    word-break: keep-all;\n  }\n  */\n  \n  .itemize > li {\n    position: relative;\n    min-height: 1.4em;\n  }\n  .itemize > li > span.li_level, .li_enumerate.not_number > span.li_level { \n    position: absolute;\n    right: 100%;\n    white-space: nowrap;\n    width: max-content;;\n    display: flex;\n    justify-content: flex-end;\n    padding-right: 10px;\n    box-sizing: border-box;\n  }\n  .li_enumerate.not_number {\n    position: relative;\n    display: inline-block;\n    list-style-type: none;\n    min-height: 1.4em;\n  }\n  .itemize > li.li_itemize[data-marker-empty=\"true\"],\n  .enumerate > li.li_enumerate[data-marker-empty=\"true\"] {\n    margin-inline-start: -18px;\n  }\n";
